sys_variable_value.value:
(function execute(inputs, outputs) {

  try {
    // 1. SETUP THE PROMPT
    // Added examples (in brackets) to help the AI decide correctly
    var prompt = "You are a utility triage expert. Analyze the description and the attached image (if any). " +
                 "Extract exactly four fields in raw JSON format: " +
                 
                 // IMPROVED DEFINITIONS
                 "'category' (Must be exactly one of: " + 
                 "'Infrastructure' [for poles, towers, lines, equipment damage], " + 
                 "'Vegetation' [for trees, branches, vines], " + 
                 "'Electrical' [for sparking, fire, smoke, power outages], " + 
                 "'Other'), " +
                 
                 "'subcategory' (Pole, Transformer, Line, Tree, Fire, Leak), " +
                 
                 "'severity_score' (integer 1-10, where 10 is an emergency), and " +
                 
                 "'confidence' (integer 0-100). " +
                 
                 "Return ONLY raw JSON: {\"category\": \"...\", \"subcategory\": \"...\", \"severity_score\": ..., \"confidence\": ...}. " +
                 "Description: " + inputs.description;

    // 2. CHECK FOR IMAGE ATTACHMENTS
    var imagePayload = null;
    var attachGR = new GlideRecord('sys_attachment');
    attachGR.addQuery('table_sys_id', inputs.sys_id);
    attachGR.addQuery('content_type', 'STARTSWITH', 'image/');
    attachGR.query();

    if (attachGR.next()) {
      var gsa = new GlideSysAttachment();
      var base64Data = gsa.getContentBase64(attachGR);
      
      imagePayload = {
        "inline_data": {
          "mime_type": attachGR.content_type.toString(),
          "data": base64Data
        }
      };
    }

    // 3. BUILD THE API PAYLOAD
    var contentsParts = [{ "text": prompt }];
    if (imagePayload) {
      contentsParts.push(imagePayload);
    }

    var requestBody = {
      "contents": [{
        "parts": contentsParts
      }]
    };

    // 4. CALL GEMINI API
    var r = new sn_ws.RESTMessageV2();
    r.setHttpMethod('POST');
    
    // Ensure this property matches your setup
    var apiKey = gs.getProperty('x_1604691_enerst_0.gemini.api.key');
    r.setEndpoint('https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash:generateContent?key=' + apiKey);
    
    r.setRequestHeader('Content-Type', 'application/json');
    r.setRequestBody(JSON.stringify(requestBody));

    var response = r.execute();
    var responseBody = response.getBody();
    var statusCode = response.getStatusCode();

    // 5. ERROR HANDLING
    if (statusCode != 200) {
      outputs.rationale = "API Error: " + statusCode;
      outputs.confidence = 0;
      return;
    }

    // 6. PARSE RESPONSE
    var parsed = JSON.parse(responseBody);
    var aiText = parsed.candidates[0].content.parts[0].text;

    // CLEANUP: Remove Markdown and extra whitespace
    aiText = aiText.replace(/```json/g, "").replace(/```/g, "").trim();
    
    var result = JSON.parse(aiText);

    // 7. MAP TO OUTPUTS
    outputs.category = result.category.toLowerCase();
    outputs.subcategory = result.subcategory;
    // Map the new Severity Score
    outputs.severity_score = (parseInt(result.severity_score, 10) || 5).toString();
    outputs.confidence = parseInt(result.confidence, 10);
    
    // Construct rationale
    outputs.rationale = "Category: " + result.category + 
                        " | Severity: " + result.severity_score + "/10" +
                        "\nConfidence: " + result.confidence + "%";

  } catch (ex) {
    outputs.rationale = "Script Error: " + ex.message;
    outputs.confidence = 0;
    gs.error("Gemini Action Error: " + ex.message);
  }

})(inputs, outputs);

