sys_script_include.script:
var GenAIIntegrationHandler = Class.create();
GenAIIntegrationHandler.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    categorizeIssue: function(descriptionArg) {
        var description = descriptionArg;

        // Handle Client-side call vs Server-side call
        if (!description && typeof this.getParameter === 'function') {
             description = this.getParameter('sysparm_description');
        }

        if (!description) { return JSON.stringify({ category: "other", severity: "1.0", confidence: "0.0" }); }

        // --- REAL GEMINI API CALL START ---
        try {
            var r = new sn_ws.RESTMessageV2('Gemini Service', 'Classify Issue');

            // Pass the user's description into the ${description} variable we made in Step 2
            r.setStringParameterNoEscape('description', description);

            var response = r.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();

            // Parse Gemini's complex JSON response
            var parsedResponse = JSON.parse(responseBody);

            // Extract the text answer (e.g., "infrastructure")
            // Path: candidates[0] -> content -> parts[0] -> text
            var aiText = parsedResponse.candidates[0].content.parts[0].text;

            // Clean up the text (remove newlines, extra spaces)
            aiText = aiText.trim().toLowerCase();

            // Default logic to assign severity based on the AI's category
            var severity = "3.0";
            if (aiText.includes("electrical")) severity = "9.0";
            else if (aiText.includes("infrastructure")) severity = "8.0";
            else if (aiText.includes("vegetation")) severity = "5.0";

            return JSON.stringify({
                category: aiText,
                severity: severity,
                confidence: "0.95" // Gemini doesn't always send confidence in this endpoint, so we estimate
            });

        } catch(ex) {
            return JSON.stringify({ category: "error", severity: "0.0", message: ex.message });
        }
        // --- REAL GEMINI API CALL END ---
    },

    type: 'GenAIIntegrationHandler'
});
